/**
 * Converts email body content from different MIME types for safe display in UI
 * Handles text/plain and text/html from Google OAuth/Gmail API
 * 
 * @param {string} content - The email body content
 * @param {string} mimeType - The MIME type of the content
 * @param {boolean} removeQuotedText - Whether to remove quoted reply text
 * @returns {string} HTML-formatted content safe for display
 */
function convertEmailBodyForDisplay(content, mimeType, removeQuotedText = true) {
  if (!content) return '';
  
  const normalized = mimeType.toLowerCase().trim();
  
  switch (normalized) {
    case 'text/plain':
      return convertPlainTextToHTML(content, removeQuotedText);
    
    case 'text/html':
      return sanitizeHTML(content, removeQuotedText);
    
    default:
      return convertPlainTextToHTML(content, removeQuotedText);
  }
}

/**
 * Removes quoted/replied content from email text
 * @param {string} text - Email text content
 * @returns {string} Text without quoted replies
 */
function removeQuotedContent(text) {
  // Remove text after "On [date] ... wrote:" pattern (common in replies)
  const replyPattern = /\n\n?On .+?wrote:\s*$/s;
  text = text.replace(replyPattern, '');
  
  // Remove text after "From: " pattern (forwarded emails)
  const forwardPattern = /\n\n?From:.+$/s;
  text = text.replace(forwardPattern, '');
  
  // Remove lines starting with > (quoted text marker)
  const lines = text.split('\n');
  const filteredLines = [];
  let inQuote = false;
  
  for (const line of lines) {
    if (line.trim().startsWith('>')) {
      inQuote = true;
      continue;
    }
    if (inQuote && line.trim() === '') {
      continue; // Skip empty lines in quoted sections
    }
    inQuote = false;
    filteredLines.push(line);
  }
  
  return filteredLines.join('\n').trim();
}

/**
 * Removes quoted/replied content from HTML
 * @param {string} html - HTML content
 * @returns {string} HTML without quoted replies
 */
function removeQuotedHTMLContent(html) {
  // Remove Gmail quote containers
  html = html.replace(/<div class="gmail_quote.*?">.*?<\/div>/gs, '');
  html = html.replace(/<blockquote.*?class="gmail_quote".*?>.*?<\/blockquote>/gs, '');
  
  // Remove general blockquote elements (often used for quotes)
  html = html.replace(/<blockquote.*?>.*?<\/blockquote>/gs, '');
  
  // Remove content after "On ... wrote:" pattern
  html = html.replace(/<div[^>]*>On .+?wrote:.*?<\/div>/gs, '');
  
  return html.trim();
}

/**
 * Converts plain text to HTML, preserving formatting
 * @param {string} text - Plain text content
 * @param {boolean} removeQuoted - Whether to remove quoted text
 * @returns {string} HTML formatted text
 */
function convertPlainTextToHTML(text, removeQuoted = true) {
  if (!text) return '';
  
  // Remove quoted content if requested
  if (removeQuoted) {
    text = removeQuotedContent(text);
  }
  
  // Escape HTML characters
  let html = text
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#039;');
  
  // Convert URLs to clickable links
  html = html.replace(
    /(https?:\/\/[^\s]+)/g,
    '<a href="$1" target="_blank" rel="noopener noreferrer">$1</a>'
  );
  
  // Convert email addresses to mailto links
  html = html.replace(
    /([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/g,
    '<a href="mailto:$1">$1</a>'
  );
  
  // Convert line breaks to <br> tags
  html = html.replace(/\n/g, '<br>');
  
  // Convert multiple spaces to non-breaking spaces
  html = html.replace(/ {2,}/g, (match) => '&nbsp;'.repeat(match.length));
  
  return html;
}

/**
 * Sanitizes HTML content to prevent XSS attacks
 * Removes dangerous tags and attributes while keeping formatting
 * @param {string} html - HTML content
 * @param {boolean} removeQuoted - Whether to remove quoted text
 * @returns {string} Sanitized HTML
 */
function sanitizeHTML(html, removeQuoted = true) {
  if (!html) return '';
  
  // Remove quoted content if requested
  if (removeQuoted) {
    html = removeQuotedHTMLContent(html);
  }
  
  // Remove script tags and their content
  html = html.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '');
  
  // Remove dangerous event handlers
  html = html.replace(/\son\w+\s*=\s*["'][^"']*["']/gi, '');
  html = html.replace(/\son\w+\s*=\s*[^\s>]*/gi, '');
  
  // Remove javascript: protocol from links
  html = html.replace(/href\s*=\s*["']javascript:[^"']*["']/gi, 'href="#"');
  
  // Remove data: protocol (can be used for XSS)
  html = html.replace(/href\s*=\s*["']data:[^"']*["']/gi, 'href="#"');
  html = html.replace(/src\s*=\s*["']data:[^"']*["']/gi, 'src=""');
  
  // Add target="_blank" and rel="noopener noreferrer" to external links
  html = html.replace(
    /<a\s+([^>]*href\s*=\s*["']https?:\/\/[^"']*["'][^>]*)>/gi,
    (match, attrs) => {
      if (!attrs.includes('target=')) {
        attrs += ' target="_blank" rel="noopener noreferrer"';
      }
      return `<a ${attrs}>`;
    }
  );
  
  // Remove potentially dangerous tags
  const dangerousTags = ['iframe', 'object', 'embed', 'form', 'input', 'button', 'textarea', 'select'];
  dangerousTags.forEach(tag => {
    const regex = new RegExp(`<${tag}\\b[^<]*(?:(?!<\\/${tag}>)<[^<]*)*<\\/${tag}>`, 'gi');
    html = html.replace(regex, '');
    html = html.replace(new RegExp(`<${tag}[^>]*>`, 'gi'), '');
  });
  
  return html;
}

/**
 * Decodes base64 content (common in Gmail API responses)
 * @param {string} base64String - Base64 encoded string
 * @returns {string} Decoded string
 */
function decodeBase64(base64String) {
  try {
    // Gmail API uses URL-safe base64, replace characters
    const base64 = base64String.replace(/-/g, '+').replace(/_/g, '/');
    return decodeURIComponent(escape(atob(base64)));
  } catch (e) {
    console.error('Error decoding base64:', e);
    return base64String;
  }
}

/**
 * Main function to process Gmail API message parts
 * Handles the structure from Gmail API responses
 * @param {Object} messagePart - Gmail API message part object
 * @param {boolean} removeQuotedText - Whether to remove quoted reply text (default: true)
 * @returns {string} HTML content for display
 */
function processGmailMessagePart(messagePart, removeQuotedText = true) {
  if (!messagePart) return '';
  
  // Check if this part has a body with data
  if (messagePart.body && messagePart.body.data) {
    const content = decodeBase64(messagePart.body.data);
    const mimeType = messagePart.mimeType || 'text/plain';
    return convertEmailBodyForDisplay(content, mimeType, removeQuotedText);
  }
  
  // If it has parts (multipart message), process them
  if (messagePart.parts && messagePart.parts.length > 0) {
    // Prefer HTML over plain text
    const htmlPart = messagePart.parts.find(p => p.mimeType === 'text/html');
    if (htmlPart && htmlPart.body && htmlPart.body.data) {
      const content = decodeBase64(htmlPart.body.data);
      return convertEmailBodyForDisplay(content, 'text/html', removeQuotedText);
    }
    
    // Fall back to plain text
    const textPart = messagePart.parts.find(p => p.mimeType === 'text/plain');
    if (textPart && textPart.body && textPart.body.data) {
      const content = decodeBase64(textPart.body.data);
      return convertEmailBodyForDisplay(content, 'text/plain', removeQuotedText);
    }
    
    // Recursively process nested parts
    for (const part of messagePart.parts) {
      const result = processGmailMessagePart(part, removeQuotedText);
      if (result) return result;
    }
  }
  
  return '';
}

/**
 * Process multiple Gmail messages from API response
 * @param {Array} messages - Array of Gmail message objects
 * @param {boolean} removeQuotedText - Whether to remove quoted text (default: true)
 * @returns {Array} Processed messages with HTML content
 */
export function processGmailMessages(messages, removeQuotedText = true) {
  return messages.map(message => ({
    id: message.id,
    threadId: message.threadId,
    snippet: message.snippet,
    labelIds: message.labelIds,
    internalDate: message.internalDate,
    historyId: message.historyId,
    htmlContent: processGmailMessagePart(message.payload, removeQuotedText),
    // Also store the raw payload if needed
    payload: message.payload
  }));
}

